
public class Sort4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		System.out.print("");
		
		for(int i=0;i<5;i++)
		{
			System.out.print(" * ");
		}
		
		
		System.out.println();
		
		for(int j=0;j<5 ;j++)
		{
			for(int x=0;x<5;x++)
			{
				System.out.print(" ");
			}
			System.out.println("  *  ");
		}
	}


}
